import React from 'react';
import { useParams } from 'react-router-dom';
import './App.css'; 

const contenutiCapitoli = [
  {
    id: 0,
    titolo: "Panorama dell'IA",
    contenuto: "Qui va il contenuto del primo capitolo, inclusi eventuali argomenti..."
  },
  {
    id: 1,
    titolo: "L'IA e la Semiotica",
    contenuto: "Qui va il contenuto del secondo capitolo, con dettagli specifici..."
  },
  // Aggiungi altri capitoli come necessario
];

const DettaglioCapitolo = () => {
  let { id } = useParams(); 
  // Trova il capitolo corrispondente all'id ottenuto dai parametri dell'URL
  const capitolo = contenutiCapitoli.find(c => c.id === parseInt(id, 10));

  return (
    <div className="box-semi-trasparente">
      {capitolo ? (
        <>
          <h2>{capitolo.titolo}</h2>
          <p>{capitolo.contenuto}</p>
        </>
      ) : (
        <p>Capitolo non trovato.</p>
      )}
    </div>
  );
};

export default DettaglioCapitolo;
