import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

import immagine1 from './immagini2/immagine1.jpg';
import immagine2 from './immagini2/immagine2.jpg';
import immagine3 from './immagini2/immagine3.jpg';
import immagine4 from './immagini2/immagine4.jpg';
import immagine5 from './immagini2/immagine5.jpg';
const immagini = [immagine1, immagine2, immagine3, immagine4, immagine5];

const Carosello = () => {
  const caroselloRef = useRef(null);
  const [isCaroselloFullyInView, setIsCaroselloFullyInView] = useState(false);
  const [caroselloScrollPosition, setCaroselloScrollPosition] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      const caroselloRect = caroselloRef.current.getBoundingClientRect();
      const isFullyInView = caroselloRect.top >= 0 && caroselloRect.bottom <= window.innerHeight;

      setIsCaroselloFullyInView(isFullyInView);

      if (isFullyInView) {
        // Calcola la posizione di scroll relativa al carosello
        const scrollDelta = currentScrollY - caroselloRect.top;
        setCaroselloScrollPosition(scrollDelta);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    if (isCaroselloFullyInView) {
      // Aggiorna la posizione delle immagini solo se il carosello è completamente visibile
      updateCaroselloImagesPosition();
    }
  }, [isCaroselloFullyInView, caroselloScrollPosition]);

  const updateCaroselloImagesPosition = () => {
    const movementSpeed = 2; // Regola la velocità di movimento delle immagini
    const maxScroll = immagini.length * 300; // Calcola lo scroll massimo basato sul numero di immagini e spazio tra di esse

    immagini.forEach((_, index) => {
      const imageElement = caroselloRef.current.children[index];
      // Calcola la nuova posizione delle immagini basata sulla posizione di scroll del carosello
      const newPosition = Math.min(maxScroll, caroselloScrollPosition * movementSpeed);
      imageElement.style.transform = `translateX(-${newPosition}px)`;
    });
  };

  return (
    <div ref={caroselloRef} className="carousel-container">
      {immagini.map((src, index) => (
        <Link key={index} to={`/dettaglio/${index}`}>
          <img src={src} alt={`Immagine ${index + 1}`} className="carosello-immagine" />
        </Link>
      ))}
    </div>
  );
};

export default Carosello;
