// Home.js
import React, { useState, useEffect } from 'react';
import Carosello from './Carosello';
import QRCode from './QRCode'; // Importa il nuovo componente QRCode

function Home() { 
  const [showQRCode, setShowQRCode] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      // Ottieni l'altezza del viewport
      const viewportHeight = window.innerHeight;
      // Ottieni l'altezza del contenuto sopra il carosello
      const caroselloTop = document.querySelector('.carousel-section').getBoundingClientRect().top;

      // Mostra il QRCode solo se il carosello è completamente scrollato fuori dal viewport
      if (caroselloTop < -viewportHeight) {
        setShowQRCode(true);
      } else {
        setShowQRCode(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <section className="contenuto-testuale">
        <h1>"Decodificare gli Stereotipi: L'Impatto delle Intelligenze Artificiali sui Messaggi Pubblicitari"</h1>
        <p>Qui troverai informazioni sulla mia tesi.</p>
      </section>
      <section className="carousel-section">
        <Carosello />
      </section>
      {showQRCode && (
        <section className='qr-code-section'>
          <QRCode /> {/* Utilizza il componente QRCode */}
        </section>
      )}
    </>
  );
}

export default Home;
