import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

// Import images immagine1 from './immagini2/immagine1.jpg';
import immagine1 from './immagini2/immagine1.jpg';
import immagine2 from './immagini2/immagine2.jpg';
import immagine3 from './immagini2/immagine3.jpg';
import immagine4 from './immagini2/immagine4.jpg';
import immagine5 from './immagini2/immagine5.jpg';

// Define the images array
const immagini = [immagine1, immagine2, immagine3, immagine4, immagine5];

const Carosello = () => {
  const caroselloRef = useRef(null);
  const [caroselloStartY, setCaroselloStartY] = useState(null);
  const [lastScrollTop, setLastScrollTop] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      const caroselloRect = caroselloRef.current.getBoundingClientRect();
      const isCaroselloInView = caroselloRect.top >= 0 && caroselloRect.bottom <= window.innerHeight;

      if (isCaroselloInView) {
        if (caroselloStartY === null) {
          setCaroselloStartY(currentScrollY);
        }
        const scrollDelta = currentScrollY - lastScrollTop;
        updateCaroselloPosition(scrollDelta);
      } else {
        setCaroselloStartY(null);
      }
      setLastScrollTop(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [caroselloStartY, lastScrollTop]);

  const updateCaroselloPosition = (scrollDelta) => {
    // Calcola la posizione orizzontale delle immagini basata su scrollDelta
    const movementSpeed = 2; // Regola la velocità di movimento delle immagini
    const maxScroll = immagini.length * 300; // Calcola lo scroll massimo basato sul numero di immagini e spazio tra di esse

    // Aggiorna la posizione di ogni immagine del carosello
    immagini.forEach((_, index) => {
      const imageElement = caroselloRef.current.children[index];
      const newPosition = Math.min(maxScroll, Math.max(0, scrollDelta * movementSpeed));
      imageElement.style.transform = `translateX(-${newPosition}px)`;
    });
  };

  return (
    <div ref={caroselloRef} className="carousel-container">
      {immagini.map((src, index) => (
        <Link key={index} to={`/dettaglio/${index}`}>
          <img src={src} alt={`Immagine ${index + 1}`} className="carosello-immagine" />
        </Link>
      ))}
    </div>
  );
};

export default Carosello;
