import React, { useState, useEffect } from 'react'; // Aggiungi useState qui
import { Link } from 'react-router-dom';

// Assicurati che le immagini siano importate correttamente
import immagine1 from './immagini2/immagine1.jpg';
import immagine2 from './immagini2/immagine2.jpg';
import immagine3 from './immagini2/immagine3.jpg';
import immagine4 from './immagini2/immagine4.jpg';
import immagine5 from './immagini2/immagine5.jpg';

// Definisci la variabile immagini come un array dei percorsi delle immagini importate
const immagini = [immagine1, immagine2, immagine3, immagine4, immagine5];

const Carosello = () => {
  const [scrollY, setScrollY] = useState(0); // useState è ora importato correttamente

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Calcola la posizione orizzontale delle immagini basata su scrollY
  const calculatePosition = (index) => {
    const startScroll = 200; // L'immagine inizia a muoversi dopo 300px di scroll

      // Calcola la distanza di scroll oltre 'startScroll'
  let scrollBeyondStart = scrollY - startScroll;

    // Assicurati che 'scrollBeyondStart' non sia mai negativo
    scrollBeyondStart = Math.max(scrollBeyondStart, 0);

    // Calcola la posizione basata su quanto l'utente ha scrollato oltre 'startScroll'
  // e su quanto ogni immagine deve "attendere" prima di iniziare a muoversi
  // basato sull'indice dell'immagine
  const movementDelay = 200; // Ogni immagine attende 200px di scroll in più dell'immagine precedente prima di muoversi
  const position = -(scrollBeyondStart - (index * movementDelay));

  // Limita la posizione per non far muovere l'immagine finché non si raggiunge la sua "soglia" di scroll
  return Math.min(position, 0);
};


  return (
    <div className="carousel-container">
      {immagini.map((src, index) => (
        <Link key={index} to={`/dettaglio/${index}`}>
          <img
            src={src}
            alt={`Immagine ${index + 1}`}
            style={{ transform: `translateX(${calculatePosition(index)}px)` }}
            className="carosello-immagine"
          />
        </Link>
      ))}
    </div>
  );
};


export default Carosello;
