import React, { useState, useEffect } from 'react'; // Aggiungi useState qui
import { Link } from 'react-router-dom';

// Assicurati che le immagini siano importate correttamente
import immagine1 from './immagini2/immagine1.jpg';
import immagine2 from './immagini2/immagine2.jpg';
import immagine3 from './immagini2/immagine3.jpg';
import immagine4 from './immagini2/immagine4.jpg';
import immagine5 from './immagini2/immagine5.jpg';

// Definisci la variabile immagini come un array dei percorsi delle immagini importate
const immagini = [immagine1, immagine2, immagine3, immagine4, immagine5];

const Carosello = () => {
  const caroselloRef = useRef(null);
  const [isCaroselloActive, setIsCaroselloActive] = useState(false);
  const [caroselloScrollPosition, setCaroselloScrollPosition] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const caroselloRect = caroselloRef.current.getBoundingClientRect();
      const isCaroselloInView = caroselloRect.top < window.innerHeight && caroselloRect.bottom >= 0;

      setIsCaroselloActive(isCaroselloInView);

      if (isCaroselloInView) {
        // Calcola la posizione di scroll relativa al carosello
        const scrollDelta = window.scrollY - caroselloRect.top;
        setCaroselloScrollPosition(scrollDelta);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const calculatePosition = (index) => {
    // Definisci un punto di scroll oltre il quale le immagini iniziano a muoversi
    const startMovingPoint = 800; // L'utente deve scrollare 800px prima che le immagini inizino a muoversi
    const movementSpeed = 0.1; // Velocità con cui le immagini si muovono rispetto allo scroll

    // Calcola quanto l'utente ha scrollato oltre il punto di inizio
    let scrollBeyondStart = Math.max(scrollY - startMovingPoint, 0);

    // Calcola la posizione finale delle immagini basata sullo scroll
    // Ogni immagine ha un ritardo basato sull'indice, quindi appariranno una alla volta
    let finalPosition = -(scrollBeyondStart * movementSpeed) + (index * 100); // '200' determina la distanza tra le immagini

    // Assicurati che le immagini inizino fuori dalla viewport sulla destra e si muovano verso sinistra
    finalPosition = Math.min(finalPosition, window.innerWidth + (index * 300));

    return finalPosition;
  };

  return (
    <div ref={caroselloRef} className="carousel-container">
      {immagini.map((src, index) => (
        <Link key={index} to={`/dettaglio/${index}`}>
          <img
            src={src}
            alt={`Immagine ${index + 1}`}
            style={{ transform: `translateX(${calculatePosition(index)}px)` }}
            className="carosello-immagine"
          />
        </Link>
      ))}
    </div>
  );
};

export default Carosello;
