import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

// Importa le immagini
const immagini = [/* percorsi delle immagini */];

const Carosello = () => {
  const caroselloRef = useRef(null);
  const [scrollY, setScrollY] = useState(0);
  const [caroselloPosition, setCaroselloPosition] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      setScrollY(currentScrollY);

      const caroselloRect = caroselloRef.current.getBoundingClientRect();
      const caroselloTop = caroselloRect.top + currentScrollY;

      if (currentScrollY >= caroselloTop && currentScrollY <= caroselloTop + caroselloRect.height) {
        // L'utente sta scrollando all'interno del carosello
        const scrollDelta = currentScrollY - caroselloTop;
        setCaroselloPosition(scrollDelta);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const calculatePosition = (index) => {
    // Calcola la posizione delle immagini basata su caroselloPosition
    // Questo esempio sposta ogni immagine di 100px per ogni pixel di scrollDelta
    return -caroselloPosition + (index * 100);
  };

  return (
    <div ref={caroselloRef} className="carousel-container">
      {immagini.map((src, index) => (
        <Link key={index} to={`/dettaglio/${index}`}>
          <img
            src={src}
            alt={`Immagine ${index + 1}`}
            style={{ transform: `translateX(${calculatePosition(index)}px)` }}
            className="carosello-immagine"
          />
        </Link>
      ))}
    </div>
  );
};

export default Carosello;
