import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

// Import images immagine1 from './immagini2/immagine1.jpg';
import immagine1 from './immagini2/immagine1.jpg';
import immagine2 from './immagini2/immagine2.jpg';
import immagine3 from './immagini2/immagine3.jpg';
import immagine4 from './immagini2/immagine4.jpg';
import immagine5 from './immagini2/immagine5.jpg';

// Define the images array
const immagini = [immagine1, immagine2, immagine3, immagine4, immagine5];

const Carosello = () => {
  const caroselloRef = useRef(null);
  const [caroselloStartY, setCaroselloStartY] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const updateCaroselloPosition = (scrollDelta) => {
    const newIndex = Math.floor(scrollDelta / (window.innerHeight * 0.6));
    const targetIndex = Math.max(0, Math.min(immagini.length - 1, newIndex));

    if (targetIndex !== currentImageIndex) {
      setCurrentImageIndex(targetIndex);
      const newScrollPosition = targetIndex * (window.innerHeight * 0.6);
      window.scrollTo(0, newScrollPosition);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      if (caroselloRef.current && caroselloStartY === null) {
        const caroselloRect = caroselloRef.current.getBoundingClientRect();
        if (caroselloRect.top < window.innerHeight && caroselloRect.bottom >= 0) {
          setCaroselloStartY(caroselloRect.top);
        }
      }

      if (caroselloStartY) {
        const scrollDelta = window.scrollY - caroselloStartY;
        updateCaroselloPosition(scrollDelta);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [caroselloStartY]);

  return (
    <div ref={caroselloRef} className="carousel-container">
      {immagini.map((src, index) => (
        <Link key={index} to={`/dettaglio/${index}`}>
          <div className="carosello-immagine-wrapper">
            <img
              src={src}
              alt={`Immagine ${index + 1}`}
              className={`carosello-immagine ${index === currentImageIndex ? 'active' : ''}`}
            />
          </div>
        </Link>
      ))}
    </div>
  );
};

export default Carosello;
