import React, { useRef, useEffect } from 'react';
import { useSpring, animated } from '@react-spring/web';
import { Link } from 'react-router-dom';

import immagine1 from './immagini2/immagine1.jpg';
import immagine2 from './immagini2/immagine2.jpg';
import immagine3 from './immagini2/immagine3.jpg';
import immagine4 from './immagini2/immagine4.jpg';
import immagine5 from './immagini2/immagine5.jpg';

const Carosello = () => {
  const [scrollY, setScrollY] = useState(0);
  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Calcola la posizione orizzontale delle immagini basata su scrollY
  const calculatePosition = (index) => {
    // Qui puoi inserire la logica per calcolare la posizione orizzontale delle immagini
    // Ad esempio, potresti voler che le immagini si muovano di più man mano che l'utente scrolla
    return -(index * 100) + scrollY / 5; // Questa è solo una formula di esempio
  };

  return (
    <div className="carousel-container">
      {immagini.map((src, index) => (
        <Link key={index} to={`/dettaglio/${index}`}>
          <img
            src={src}
            alt={`Immagine ${index + 1}`}
            style={{ transform: `translateX(${calculatePosition(index)}px)` }}
            className="carosello-immagine"
          />
        </Link>
      ))}
    </div>
  );
};


export default Carosello;
