import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import logoImage from './immagini2/logounite.jpeg';
import umanoideImage from './immagini2/umanoide.png';

const NeuralScene = ({ isScrolled }) => {
  const mountRef = useRef(null);
  const umanoideRef = useRef(); // Aggiungi questa linea
  const cubeRef = useRef(); // Aggiungi questa linea per mantenere il riferimento al cubo


  useEffect(() => {
    const currentRef = mountRef.current;
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(50, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.z = 5;

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    if (currentRef) {
      currentRef.appendChild(renderer.domElement);
    }

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableZoom = false;

    const textureLoader = new THREE.TextureLoader();
    textureLoader.load(logoImage, function (texture) {
      const material = new THREE.MeshBasicMaterial({ map: texture });
      const materials = Array(6).fill(material);
      const cubeGeometry = new THREE.BoxGeometry(0.5, 0.5, 0.5);
      const cube = new THREE.Mesh(cubeGeometry, materials);
      cube.position.set(isScrolled ? 2 : 0, isScrolled ? 2 : 1, 1);
      scene.add(cube);
      cubeRef.current = cube; // Aggiorna il riferimento qui

    });
// Carica l'immagine dell'umanoide come texture su un cubo trasparente
textureLoader.load(umanoideImage, function (texture) {
  const geometry = new THREE.BoxGeometry(2, 2, 2); // Ingrandito del doppio
  const material = new THREE.MeshBasicMaterial({ map: texture, transparent: true });
  const umanoide = new THREE.Mesh(geometry, material);
  umanoide.position.set(-4, 0, 0); // Spostato tre volte a destra
  scene.add(umanoide);
  umanoideRef.current = umanoide;
});

    // Particelle
    const particlesGeometry = new THREE.BufferGeometry();
    const particlesCount = 10000;
    const posArray = new Float32Array(particlesCount * 3);
    for (let i = 0; i < particlesCount * 3; i++) {
      posArray[i] = (Math.random() - 0.5) * (Math.random() * 5);
    }
    particlesGeometry.setAttribute('position', new THREE.BufferAttribute(posArray, 3));

    const particlesMaterial = new THREE.PointsMaterial({ size: 0.007 });
    const particlesMesh = new THREE.Points(particlesGeometry, particlesMaterial);
    scene.add(particlesMesh);

    // Luci
    const light = new THREE.AmbientLight(0x404040);
    scene.add(light);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(0, 0, 0);
    scene.add(directionalLight);

    const animate = () => {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    };
    animate();

    const onWindowResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', onWindowResize, false);

    // Logica per rendere l'umanoide cliccabile
    const onClick = (event) => {
      const mouse = new THREE.Vector2(
        (event.clientX / window.innerWidth) * 2 - 1,
        -(event.clientY / window.innerHeight) * 2 + 1
      );

      const raycaster = new THREE.Raycaster();
      raycaster.setFromCamera(mouse, camera);
      const intersects = raycaster.intersectObjects(scene.children);

      for (let i = 0; i < intersects.length; i++) {
        if (intersects[i].object === umanoideRef.current) {
          window.location.href = 'https://gemini-demo-52mps1ocj-alessandros-projects-ece31532.vercel.app';
          break;
        }
      }
    };

    window.addEventListener('click', onClick);

    return () => {
      window.removeEventListener('resize', onWindowResize);
      window.removeEventListener('click', onClick);
      currentRef.removeChild(renderer.domElement);
    };
  }, [isScrolled]); // Aggiungi umanoideRef.current alle dipendenze se necessario

  return <div ref={mountRef} className="scene-container" />;
};

export default NeuralScene;
