import React, { useState, useEffect } from 'react'; // Aggiungi useState qui
import { Link } from 'react-router-dom';

// Assicurati che le immagini siano importate correttamente
import immagine1 from './immagini2/immagine1.jpg';
import immagine2 from './immagini2/immagine2.jpg';
import immagine3 from './immagini2/immagine3.jpg';
import immagine4 from './immagini2/immagine4.jpg';
import immagine5 from './immagini2/immagine5.jpg';

// Definisci la variabile immagini come un array dei percorsi delle immagini importate
const immagini = [immagine1, immagine2, immagine3, immagine4, immagine5];

const Carosello = () => {
  const [scrollY, setScrollY] = useState(0); // useState è ora importato correttamente

  useEffect(() => {
    const handleScroll = () => {
      // Aggiorna scrollY solo se supera una certa soglia (es. 1000px)
      if (window.scrollY > 1000) {
        setScrollY(window.scrollY - 1000);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const calculatePosition = (index) => {
    // Calcola la posizione delle immagini in modo che inizino fuori dalla viewport
    // e si muovano verso sinistra in base allo scrollY
    const imageWidth = 275; // Assumi che ogni immagine sia larga 275px
    const gap = 200; // Gap di 200px tra le immagini
    const initialOffset = window.innerWidth; // Inizia fuori dalla viewport
    const movementSpeed = 0.3; // Velocità di movimento delle immagini

    // Calcola la posizione orizzontale basata su scrollY
    let position = initialOffset + (index * (imageWidth + gap)) - (scrollY * movementSpeed);
    return Math.min(position, initialOffset + (index * (imageWidth + gap))); // Evita che le immagini si muovano troppo a sinistra
  };



  return (
    <div className="carousel-container" style={{ overflowX: 'hidden', whiteSpace: 'nowrap' }}>
      {immagini.map((src, index) => (
        <Link key={index} to={`/dettaglio/${index}`}>
          <img
            src={src}
            alt={`Immagine ${index + 1}`}
            style={{ transform: `translateX(${calculatePosition(index)}px)`, display: 'inline-block'  }}
            className="carosello-immagine"
          />
        </Link>
      ))}
    </div>
  );
};


export default Carosello;
