import React, { useState, useEffect, useRef } from 'react'; // Aggiungi useRef qui
import { Link } from 'react-router-dom';

// Importa le immagini
import immagine1 from './immagini2/immagine1.jpg';
import immagine2 from './immagini2/immagine2.jpg';
import immagine3 from './immagini2/immagine3.jpg';
import immagine4 from './immagini2/immagine4.jpg';
import immagine5 from './immagini2/immagine5.jpg';

// Definisci la variabile immagini come un array dei percorsi delle immagini importate
const immagini = [immagine1, immagine2, immagine3, immagine4, immagine5];


const Carosello = () => {
  const caroselloRef = useRef(null);
  const [scrollY, setScrollY] = useState(0);
  const [isScrollingCarosello, setIsScrollingCarosello] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      setScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    const checkCaroselloLimits = () => {
      if (!caroselloRef.current) return;

      const caroselloWidth = caroselloRef.current.scrollWidth;
      const viewportWidth = window.innerWidth;
      const firstImageRect = caroselloRef.current.children[0].getBoundingClientRect();
      const lastImageRect = caroselloRef.current.children[caroselloRef.current.children.length - 1].getBoundingClientRect();

      // Controlla se l'ultima immagine è completamente visibile
      if (lastImageRect.right <= viewportWidth) {
        setIsScrollingCarosello(false); // Rilascia lo scroll della pagina
      }
      // Controlla se la prima immagine è completamente visibile
      else if (firstImageRect.left >= 0) {
        setIsScrollingCarosello(false); // Rilascia lo scroll della pagina
      } else {
        setIsScrollingCarosello(true); // Blocca lo scroll della pagina
      }
    };

    window.addEventListener('scroll', checkCaroselloLimits);

    return () => {
      window.removeEventListener('scroll', checkCaroselloLimits);
    };
  }, [scrollY]);

  useEffect(() => {
    if (isScrollingCarosello) {
      // Disabilita lo scroll della pagina
      document.body.style.overflow = 'hidden';
    } else {
      // Riabilita lo scroll della pagina
      document.body.style.overflow = '';
    }
  }, [isScrollingCarosello]);

  return (
    <div ref={caroselloRef} className="carousel-container">
      {immagini.map((src, index) => (
        <Link key={index} to={`/dettaglio/${index}`}>
          <img
            src={src}
            alt={`Immagine ${index + 1}`}
            className="carosello-immagine"
          />
        </Link>
      ))}
    </div>
  );
};

export default Carosello;
