import React from 'react';
import { useParams } from 'react-router-dom';
import './App.css'; 

const DettaglioCapitolo = () => {
  let { id } = useParams(); 
  return (
    <div className="box-semi-trasparente">
      <h2>Dettaglio del Capitolo {id}</h2>
      <div style={{ display: 'flex' }}>
        {/* Colonna delle Affermazioni di Cruciani */}
        <div style={{ flex: 1, paddingRight: '10px' }}>
          <p><strong>Affermazione di Cruciani:</strong></p>
          <p>"L'intelligenza artificiale non riesce a creare..."</p>
          {/* Altre affermazioni... */}
        </div>

        {/* Colonna degli Esempi Contestanti */}
        <div style={{ flex: 1, paddingLeft: '10px' }}>
          <p><strong>Esempio Contestante:</strong></p>
          <p>AlphaGo che ha battuto Lee Sedol nel 2016 dimostra...</p>
          {/* Altri esempi... */}
        </div>
      </div>
      {/* Navigazione */}
      <a href="/#">Torna al Carosello</a> | <a href="/dettaglio/1">Capitolo Successivo</a>
    </div>
  );
};

export default DettaglioCapitolo;
